import React from 'react';
import { MathJax, MathJaxContext } from 'better-react-mathjax';

const FormulaDisplay = ({ base, exponentsAndOperators }) => {
  // Constructing the formula in LaTeX format
  let formula = `${base}^{${exponentsAndOperators[0]}}`;
  
  for (let i = 1; i < exponentsAndOperators.length; i += 2) {
    formula += ` \\frac{${base}^{${exponentsAndOperators[i+1]}}}{${base}^{${exponentsAndOperators[i-1]}}}`;
  }

  return (
    <MathJaxContext>
      <MathJax dynamic>
        {`\\[${formula}\\]`}
      </MathJax>
    </MathJaxContext>
  );
};

// Usage
const input = [5, [-8, "/", 1, "/", 6, "/", 8]];

const App = () => {
  return (
    <div>
      <h1>Formula:</h1>
      <FormulaDisplay base={input[0]} exponentsAndOperators={input[1]} />
    </div>
  );
};

export default App;
