import React, { useState, useEffect, useRef } from 'react';
import { ResponsiveContainer, Tooltip, Rectangle, XAxis, YAxis, ScatterChart, Scatter } from 'recharts';
import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL,
  withCredentials: true,
});

const HeatMap = () => {
  const [heatmapData, setHeatmapData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [containerSize, setContainerSize] = useState({ width: 0, height: 0 });
  const containerRef = useRef(null);

  useEffect(() => {
    const fetchHeatmapData = async () => {
      try {
        setLoading(true);
        const response = await api.get('/get-user-heatmap');
        const formattedData = response.data.map(item => ({
          x: parseInt(item.x),
          y: parseInt(item.y),
          value: item.correct_rate === '-' ? null : parseFloat(item.correct_rate)
        }));
        setHeatmapData(formattedData);
      } catch (error) {
        console.error("Error fetching heatmap data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchHeatmapData();
  }, []);

  useEffect(() => {
    const updateSize = () => {
      if (containerRef.current) {
        setContainerSize({
          width: containerRef.current.offsetWidth,
          height: containerRef.current.offsetHeight
        });
      }
    };

    updateSize();
    window.addEventListener('resize', updateSize);
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  const getColor = (value) => {
    if (value === null) return '#000000'; // Color for no data
    const red = Math.floor(255 * (1 - value));
    const green = Math.floor(255 * value);
    return `rgb(${red},${green},0)`; // Gradient from red to green
  };

  const CustomizedShape = (props) => {
    const { x, y, width, height, value } = props;
    return (
      <Rectangle
        x={x}
        y={y}
        width={width}
        height={height}
        fill={getColor(value)} // Fill color based on value
      />
    );
  };

  if (loading) {
    return <div>Loading heatmap data...</div>;
  }

  return (
    <div ref={containerRef} className="w-full h-96">
      {containerSize.width > 0 && containerSize.height > 0 && (
        <ResponsiveContainer width="100%" height="100%">
          <ScatterChart
            margin={{
              top: 20,
              right: 20,
              bottom: 20,
              left: 20,
            }}
          >
            <XAxis type="number" dataKey="x" name="x" unit="" />
            <YAxis type="number" dataKey="y" name="y" unit="" />
            <Tooltip 
              cursor={{ strokeDasharray: '3 3' }}
              content={({ payload }) => {
                if (payload && payload.length) {
                  const data = payload[0].payload;
                  return (
                    <div className="custom-tooltip bg-white p-2 border border-gray-300 rounded">
                      <p>{`X: ${data.x}, Y: ${data.y}`}</p>
                      <p>{`Correct Rate: ${data.value !== null ? data.value.toFixed(2) : 'N/A'}`}</p>
                    </div>
                  );
                }
                return null;
              }}
            />
            <Scatter data={heatmapData} shape={<CustomizedShape />} />
          </ScatterChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default HeatMap;
