import React, { useState, useEffect } from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, ResponsiveContainer } from 'recharts';
import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL,
  withCredentials: true,
});

const HeatMap = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchHeatmapData = async () => {
      try {
        const response = await api.get('/get-heatmap-data');
        setData(response.data);
      } catch (error) {
        console.error("Error fetching heatmap data:", error);
      }
    };

    fetchHeatmapData();
  }, []);

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const { x, y, z } = payload[0].payload;
      return (
        <div className="custom-tooltip bg-white text-black p-2 rounded shadow">
          <p>{`${x} x ${y} = ${x * y}`}</p>
          <p>{`Correct: ${z.toFixed(2)}%`}</p>
        </div>
      );
    }
    return null;
  };

  return (
    <ResponsiveContainer width="100%" height={400}>
      <ScatterChart margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
        <XAxis type="number" dataKey="x" name="x" domain={[1, 10]} />
        <YAxis type="number" dataKey="y" name="y" domain={[1, 10]} />
        <ZAxis type="number" dataKey="z" range={[0, 1000]} />
        <Tooltip content={<CustomTooltip />} />
        <Scatter
          data={data}
          fill="#8884d8"
          shape="circle"
        />
      </ScatterChart>
    </ResponsiveContainer>
  );
};

export default HeatMap;
