import React, { useState } from 'react';
import './App.css';

function App() {
  const [count, setCount] = useState(0);


    const frogImages = {
      Option1: '/frogs/frog1.jpg',
      Option2: '/frogs/frog2.jpg',
      Option3: '/frogs/frog3.jpg',
      Option4: '/frogs/frog4.jpg',
    };
  const increment = () => {
    setCount((prevCount) => (prevCount + 1) % frogImages.length);
  };

  const decrement = () => {
    if (count > 0){
      setCount(count - 1);
    }
  };

  const [selectedOption, setSelectedOption] = useState('Option1');
  const options = ['Options1', 'Option2', 'Option3', 'Option4'];

  const handleChange = (e) => {
    setSelectedOption(e.target.value)
  }

  return (
    <div className="App">
      <h1>Simple Counter App</h1>
      <p>Current Count: {count}</p>
      <button onClick={increment}>Increase</button>
      <button onClick={decrement}>Decrease</button>
      <select value={selectedOption} onChange={handleChange}>
        {options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}

      </select>
      <div>
        <h2>Selected Option: {selectedOption}</h2>
        <img src={frogImages[count]} alt={`Frog ${count + 1}`} />
      </div>
    </div>
  );
}

export default App;
