import React, { useState, useEffect } from 'react';
import { ResponsiveContainer, XAxis, YAxis, Tooltip } from 'recharts';
import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL,
  withCredentials: true,
});

const HeatMap = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchHeatmapData = async () => {
      try {
        const response = await api.get('/get-user-heatmap');
        const processedData = response.data.map(item => ({
          x: item.x,
          y: item.y,
          value: item.correct_rate === '-' ? null : parseFloat(item.correct_rate)
        }));
        setData(processedData);
      } catch (error) {
        console.error("Error fetching heatmap data:", error);
      }
    };

    fetchHeatmapData();
  }, []);

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const { x, y, value } = payload[0].payload;
      return (
        <div className="custom-tooltip bg-white text-black p-2 rounded shadow">
          <p>{`${x} x ${y} = ${x * y}`}</p>
          <p>{value !== null ? `Correct: ${(value * 100).toFixed(1)}%` : 'No data'}</p>
        </div>
      );
    }
    return null;
  };

  const colorScale = (value) => {
    if (value === null) return '#f0f0f0'; // Light gray for no data
    const hue = ((value) * 120).toString(10);
    return `hsl(${hue}, 80%, 50%)`;
  };

  const cellSize = 40;
  const margin = { top: 20, right: 20, bottom: 30, left: 30 };

  return (
    <ResponsiveContainer width="100%" height={500}>
      <svg width="100%" height="100%">
        <g transform={`translate(${margin.left},${margin.top})`}>
          {data.map((item) => (
            <rect
              key={`cell-${item.x}-${item.y}`}
              x={(item.x - 1) * cellSize}
              y={(item.y - 1) * cellSize}
              width={cellSize}
              height={cellSize}
              fill={colorScale(item.value)}
              stroke="#fff"
              strokeWidth={1}
            >
              <title>{`${item.x} x ${item.y} = ${item.x * item.y}
${item.value !== null ? `Correct: ${(item.value * 100).toFixed(1)}%` : 'No data'}`}</title>
            </rect>
          ))}
          {[...Array(10)].map((_, i) => (
            <text
              key={`x-label-${i + 1}`}
              x={(i + 0.5) * cellSize}
              y={10 * cellSize + 20}
              textAnchor="middle"
              fill="#333"
            >
              {i + 1}
            </text>
          ))}
          {[...Array(10)].map((_, i) => (
            <text
              key={`y-label-${i + 1}`}
              x={-10}
              y={(i + 0.5) * cellSize}
              textAnchor="end"
              alignmentBaseline="middle"
              fill="#333"
            >
              {i + 1}
            </text>
          ))}
        </g>
      </svg>
    </ResponsiveContainer>
  );
};

export default HeatMap;
