import React, { useState } from 'react';
import './App.css';

function App() {
  const [frogImage, setFrogImage] = useState('/frogs/frog1.jpg');

  // Fetch a random frog image from Unsplash
  const getRandomFrogImage = async () => {
    try {
      // Append a random query parameter to prevent caching
      const response = await fetch(`https://source.unsplash.com/featured/?frog&random=${Math.random()}`);
      setFrogImage(response.url); // Unsplash API returns the image URL in the response
    } catch (error) {
      console.error('Error fetching the frog image:', error);
    }
  };

  return (
    <div className="App">
      <h1>Catch a Random Frog</h1>

      {/* Display the frog image */}
      <div>
        <img src={frogImage} alt="Random Frog" style={{ width: '300px', height: '300px' }} />
      </div>

      {/* Button to fetch a random frog image */}
      <button onClick={getRandomFrogImage}>Let's Go Catch Another One</button>
    </div>
  );
}

export default App;
