import React, { useEffect, useRef } from 'react';

const NumberPresenter = ({ base, exponentsAndOperators }) => {
  const containerRef = useRef(null);

  const generateFormula = () => {
    let formula = `${base}^{${exponentsAndOperators[0]}}`;

    for (let i = 1; i < exponentsAndOperators.length; i += 2) {
      if (exponentsAndOperators[i] === '/') {
        formula += ` \\cdot ${base}^{${exponentsAndOperators[i+1]}}`;
      } else {
        formula += ` ${exponentsAndOperators[i]} ${base}^{${exponentsAndOperators[i+1]}}`;
      }
    }

    return `\\[${formula}\\]`;
  };

  useEffect(() => {
    if (containerRef.current && window.MathJax) {
      window.MathJax.typesetPromise([containerRef.current]);
    }
  }, [base, exponentsAndOperators]);

  return (
    <div ref={containerRef} className="p-4 bg-gray-100 rounded-lg">
      {generateFormula()}
    </div>
  );
};

export default NumberPresenter;
