import React, { useEffect, useState } from 'react';
import './App.css';

const QuizApp = () => {
  const [menuVisible, setMenuVisible] = useState(true);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [isFinished, setIsFinished] = useState(false);
  const [missedQuestions, setMissedQuestions] = useState([]);

  // Gångertabell game state
  const [isGångertabell, setIsGångertabell] = useState(false);
  const [gangerQuestion, setGangerQuestion] = useState({});
  const [gangerAnswers, setGangerAnswers] = useState([]);
  const [gangerScore, setGangerScore] = useState(0);
  const [gangerAttempts, setGangerAttempts] = useState(0);
  const [gangerGameOver, setGangerGameOver] = useState(false);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await fetch('/question.json');
      const data = await response.json();
      const shuffledQuestions = data.sort(() => Math.random() - 0.5);
      setQuestions(shuffledQuestions);
    };
    fetchQuestions();
  }, []);

  useEffect(() => {
    if (isGångertabell) {
      generateGangerQuestion();
    }
  }, [isGångertabell]);

  const generateGangerQuestion = () => {
    const num1 = Math.floor(Math.random() * 10) + 1;
    const num2 = Math.floor(Math.random() * 10) + 1;
    const correctAnswer = num1 * num2;
    const wrongAnswers = new Set();

    while (wrongAnswers.size < 3) {
      const wrongAnswer = Math.floor(Math.random() * 100);
      if (wrongAnswer !== correctAnswer) {
        wrongAnswers.add(wrongAnswer);
      }
    }

    const allAnswers = Array.from(wrongAnswers).concat(correctAnswer);
    const shuffledAnswers = allAnswers.sort(() => Math.random() - 0.5);

    setGangerQuestion({ num1, num2, correctAnswer });
    setGangerAnswers(shuffledAnswers);
  };

  const handleGangerAnswer = (selectedOption) => {
    setGangerAttempts((prev) => prev + 1);
    if (selectedOption === gangerQuestion.correctAnswer) {
      setGangerScore((prev) => prev + 1);
    }
    generateGangerQuestion();
  };

  const startGangerGame = () => {
    setIsGångertabell(true);
    setGangerScore(0);
    setGangerAttempts(0);
    setGangerGameOver(false);
  };

  const endGangerGame = () => {
    setIsGångertabell(false);
    setGangerGameOver(true);
  };

  const handleAnswer = (selectedOption) => {
    setUserAnswers((prev) => [...prev, selectedOption]);
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setIsFinished(true);
      checkMissedQuestions();
    }
  };

  const checkMissedQuestions = () => {
    const missed = questions.filter((question, index) => userAnswers[index] !== question.correct_answer);
    setMissedQuestions(missed);
  };

  const correctAnswersCount = () => {
    return userAnswers.filter((answer, index) => answer === questions[index].correct_answer).length;
  };

  const restartQuiz = () => {
    setUserAnswers([]);
    setCurrentQuestionIndex(0);
    setIsFinished(false);
    const shuffledMissedQuestions = missedQuestions.sort(() => Math.random() - 0.5);
    setQuestions(shuffledMissedQuestions);
  };

  const startQuiz = (category) => {
    if (category === 'Kvadratrot') {
      const kvadratrotQuestions = questions.filter(q => q.category === 'Kvadratrot');
      setQuestions(kvadratrotQuestions);
      setCurrentQuestionIndex(0);
      setIsFinished(false);
      setMenuVisible(false);
    }
  };

  if (menuVisible) {
    return (
      <div className="App">
        <h1>Main Menu</h1>
        <button onClick={() => startQuiz('Kvadratrot')}>Kvadratrot</button>
        <button onClick={startGangerGame}>Gångertabell</button>
      </div>
    );
  }

  if (isFinished) {
    return (
      <div className="App">
        <h1>Quiz Finished!</h1>
        <p>Correct Answers: {correctAnswersCount()} out of {questions.length}</p>
        <h2>Results</h2>
        <table>
          <thead>
            <tr>
              <th>Question</th>
              <th>Your Answer</th>
              <th>Correct Answer</th>
              <th>Result</th>
            </tr>
          </thead>
          <tbody>
            {questions.map((question, index) => {
              const userAnswer = userAnswers[index];
              const isCorrect = userAnswer === question.correct_answer;
              return (
                <tr key={index}>
                  <td>{question.text}</td>
                  <td>{userAnswer || 'N/A'}</td>
                  <td>{question.correct_answer}</td>
                  <td>{isCorrect ? 'Correct' : 'Incorrect'}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <button onClick={restartQuiz}>Try Failed Questions</button>
        <button onClick={() => setMenuVisible(true)}>Back to Menu</button>
      </div>
    );
  }

  if (isGångertabell) {
    if (gangerGameOver) {
      const percentage = (gangerScore / gangerAttempts) * 100;
      return (
        <div className="App">
          <h1>Game Over!</h1>
          <p>Correct Answers: {gangerScore} out of {gangerAttempts}</p>
          <p>Percentage: {percentage.toFixed(2)}%</p>
          <button onClick={() => setMenuVisible(true)}>Back to Menu</button>
        </div>
      );
    }

    return (
      <div className="App">
        <h2>{gangerQuestion.num1} x {gangerQuestion.num2} = ?</h2>
        <div>
          {gangerAnswers.map((answer, index) => (
            <button key={index} onClick={() => handleGangerAnswer(answer)}>
              {answer}
            </button>
          ))}
        </div>
      </div>
    );
  }

  if (questions.length === 0) {
    return <div className="App">Loading questions...</div>;
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="App">
      <h2>{currentQuestion.text}</h2>
      <div>
        {currentQuestion.options.map((option, index) => (
          <button key={index} onClick={() => handleAnswer(option)}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
};

export default QuizApp;
