import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Chart, registerables } from 'chart.js';
import { MatrixController, MatrixElement } from 'chartjs-chart-matrix'; // Import heatmap components

Chart.register(...registerables, MatrixController, MatrixElement);

const Home = () => {
  const navigate = useNavigate();
  const [showHeatmap, setShowHeatmap] = useState(false);
  const heatmapCanvasRef = useRef(null); // Create a ref for the canvas

  const handleGameClick = () => {
    navigate('/Gangertabell'); // Navigate to Game page
  };

  const handleRegisterClick = () => {
    navigate('/Register'); // Navigate to Register page
  };

  const handleLoginClick = () => {
    navigate('/Login'); // Navigate to Login page
  };

  const handleHeatmapClick = () => {
    setShowHeatmap((prev) => !prev); // Toggle heatmap visibility
  };

  // Sample data for the heatmap
  const heatmapData = {
    labels: ['A', 'B', 'C', 'D', 'E'], // X-axis labels
    datasets: [
      {
        label: 'Heatmap',
        data: [
          { x: 'A', y: 'A', v: 10 },
          { x: 'B', y: 'A', v: 20 },
          { x: 'C', y: 'A', v: 30 },
          { x: 'A', y: 'B', v: 40 },
          // Add more data points as needed
        ],
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
    ],
  };

  useEffect(() => {
    if (showHeatmap && heatmapCanvasRef.current) {
      const ctx = heatmapCanvasRef.current.getContext('2d');
      new Chart(ctx, {
        type: 'matrix',
        data: heatmapData,
        options: {
          responsive: true,
          scales: {
            x: {
              type: 'category',
              labels: heatmapData.labels,
            },
            y: {
              type: 'category',
              labels: heatmapData.labels,
            },
          },
        },
      });
    }
  }, [showHeatmap]); // Only run when the heatmap is shown

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Welcome to the Application</h1>
      <div className="space-x-2">
        <button className="bg-blue-500 text-white px-4 py-2 rounded" onClick={handleGameClick}>Gångertabell</button>
        <button className="bg-green-500 text-white px-4 py-2 rounded" onClick={handleRegisterClick}>Registrera användare</button>
        <button className="bg-yellow-500 text-white px-4 py-2 rounded" onClick={handleLoginClick}>Logga in</button>
        <button className="bg-purple-500 text-white px-4 py-2 rounded" onClick={handleHeatmapClick}>Visa heatmap</button>
      </div>

      {showHeatmap && (
        <div style={{ marginTop: '20px' }}>
          <h2 className="text-xl font-semibold">Heatmap</h2>
          <canvas ref={heatmapCanvasRef} style={{ width: '400px', height: '400px' }}></canvas>
        </div>
      )}
    </div>
  );
};

export default Home;
