import React, { useEffect, useState } from 'react';

const Game = () => {
  const [questionData, setQuestionData] = useState(null);
  const [refresh, setRefresh] = useState(0); // State to force a re-fetch


  useEffect(() => {
    fetch('http://localhost:8000/get-question')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log(data); // Log the fetched data here
        setQuestionData(data);
      })
      .catch(error => console.error('Error fetching question:', error));
  }, [refresh]); // Run effect every time 'refresh' changes


  if (!questionData) return <div>Loading...</div>;

  return (
    <div>
      <h2>{questionData.question}</h2>
      {questionData.answers.map((answer, index) => (
        <button key={index} onClick={handleRefresh}>{answer}</button>
      ))}
    </div>
  );
};

export default Game;
