import React, { useState } from 'react';
import './App.css';

function App() {
  const [count, setCount] = useState(0);

  // Define a list of frog images
  const [frogImage, setFrogImage] = useState('/frogs/frog1.jpg');

  const getRandomFrogImage = async () => {
    try {
      const response = await fetch('https://source.unsplash.com/featured/?frog');
      setFrogImage(response.url);
    } catch (error) {
      console.error('Error catchin frogs', error);
    }
  }

  const increment = () => {
    // Ensure count does not exceed the length of the frog images array
    setCount((prevCount) => (prevCount + 1) % frogImages.length);
  };

  const decrement = () => {
    if (count > 0) {
      setCount((prevCount) => prevCount - 1);
    }
  };

  return (
    <div className="App">
      <h1>Simple Counter App</h1>
      <p>Current Count: {count}</p>
      <button onClick={increment}>Increase</button>
      <button onClick={decrement}>Decrease</button>

      {/* Display the frog image based on the current count */}
      <div>
        <h2>Frog Image:</h2>
        <img src={frogImages[count]} alt={`Frog ${count + 1}`} />
      </div>
      <div>
        <button onClick={getRandomFrogImage}>Let's go catch a froggerman</button>
      </div>
    </div>
  );
}

export default App;
