import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Chart, registerables } from 'chart.js';
import { MatrixController, MatrixElement } from 'chartjs-chart-matrix'; // Import heatmap components

Chart.register(...registerables, MatrixController, MatrixElement);
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL,
  withCredentials: true,
});

const Home = () => {
  const navigate = useNavigate();
  const [showHeatmap, setShowHeatmap] = useState(false);
  };
  const handleGameClick = () => {
    navigate('/Gangertabell'); // Navigate to Game page
  };

  const handleRegisterClick = () => {
    navigate('/Register'); // Navigate to Register page
  };

  const handleLoginClick = () => {
    navigate('/Login'); // Navigate to Login page
  };

  const handleHeatmapClick = () => {
    setShowHeatmap((prev) => !prev); // Toggle heatmap visibility
  };
  
  const Inspiration = () => {
    const [quote, setQuote] = useState('');
  
    useEffect(() => {
      const fetchQuote = async () => {
        const response = await fetch('http://localhost:8000/get-inspiration'); // Justera URL om du anv채nder en annan port
        const data = await response.json();
        setQuote(data.quote);
      };
  
      fetchQuote();
    }, []);
  // Sample data for the heatmap
  const heatmapData = {
    labels: ['A', 'B', 'C', 'D', 'E'], // X-axis labels
    datasets: [
      {
        label: 'Heatmap',
        data: [
          { x: 'A', y: 'A', v: 10 },
          { x: 'B', y: 'A', v: 20 },
          { x: 'C', y: 'A', v: 30 },
          { x: 'A', y: 'B', v: 40 },
          // Add more data points as needed
        ],
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
    ],
  };

  return (
    <div>
      <h1> </h1>
      <button onClick={handleGameClick}>G책ngertabell</button>
      <button onClick={handleRegisterClick}>Registrera anv채ndare</button>
      <button onClick={handleLoginClick}>Logga in</button>
      <button onClick={handleHeatmapClick}>Visa heatmap</button>

      {showHeatmap && (
        <div style={{ marginTop: '20px' }}>
          <h2>Heatmap</h2>
          <canvas id="heatmapCanvas"></canvas>
          <script>
            {`const ctx = document.getElementById('heatmapCanvas').getContext('2d');
              new Chart(ctx, {
                type: 'matrix',
                data: ${JSON.stringify(heatmapData)},
                options: {
                  responsive: true,
                  scales: {
                    x: {
                      type: 'category',
                      labels: ${JSON.stringify(heatmapData.labels)},
                    },
                    y: {
                      type: 'category',
                      labels: ${JSON.stringify(heatmapData.labels)},
                    },
                  },
                },
              });`}
          </script>
        </div>
      )}
    </div>
  );
};

export default Home;
